import { useEffect, useState } from "react";
import { Button, Col, Container, Row, Table } from "react-bootstrap";



const Dashboard = () =>{

    const [empleados, setEmpleados] = useState([]);

    useEffect(() =>{
        const fetchEmpleados = async() =>{
            try {
                const response = await fetch("http://localhost:8080/api/listaEmpleados");
                const data =await response.json();

                setEmpleados(data);
            } catch (error) {
                console.log("Error al cargar los empleados: " , error.message);
            }
        }

        fetchEmpleados();
    })


    return (
        <>
        <Container className="mt-5">
            <Row>
                <Col>
                <h1 className="text-center" > Empleados</h1>
                <Table striped border hover responsive>
                    <thead>
                        <tr>
                            <th>Nombre</th>
                            <th>Email</th>
                            <th>Departmento</th>
                            <th>Teléfono</th>
                            <th>Acción</th>
                        </tr>
                        <tbody>
                        {empleados.map((empleado) =>(
                            <tr key={empleado.id}>
                                <td>{empleado.name}</td>
                                <td>{empleado.email}</td>
                                <td>{empleado.department}</td>
                                <td>{empleado.phone}</td>
                                <td>
                                    <Button variant="outline-secondary">Editar</Button>{" "}
                                    <Button variant="outline-danger">Eliminar</Button>
                                </td>


                            </tr>
                        ))}
                        </tbody>
                    </thead>
                </Table>
                </Col>
            </Row>
        </Container>
        </>
    )
}

export default Dashboard;
